{{Note
|user= BrotherhoodOfHam
|timestamp= 20140930173600
|text= This function is only used for dialogs which have background UAV tiles (See the in game pause menu for example), since this is only really found in official dialogs documentation on this function doesn't really exist.<br>
To set up your own dialog for use with this function it is actually fairly simple. Use the below example as a framework:

<syntaxhighlight lang="cpp"></syntaxhighlight><!-- this is a wiki hack so syntaxhighlight would work even within a spoiler tag -->
<spoiler text="Click to view code">
<syntaxhighlight lang="cpp">
// base classes
class RscControlsGroup
{
	class VScrollbar
	{
		color[] = {1,1,1,1};
		width = 0.021;
		autoScrollEnabled = 1;

		colorActive[] = {1, 1, 1, 1};
		colorDisabled[] = {1, 1, 1, 0.3};
		thumb = "\A3\ui_f\data\gui\cfg\scrollbar\thumb_ca.paa";
		arrowFull = "\A3\ui_f\data\gui\cfg\scrollbar\arrowFull_ca.paa";
		arrowEmpty = "\A3\ui_f\data\gui\cfg\scrollbar\arrowEmpty_ca.paa";
		border = "\A3\ui_f\data\gui\cfg\scrollbar\border_ca.paa";
	};

	class HScrollbar : VScrollbar
	{
		color[] = {1,1,1,1};
		height = 0.028;
	};

	class Controls {};

	type = 15;
	idc = -1;
	x = 0;
	y = 0;
	w = 1;
	h = 1;
	shadow = 0;
	style = 16;
};

class RscControlsGroupNoScrollbars : RscControlsGroup
{
	class VScrollbar : VScrollbar
	{
		width = 0;
	};

	class HScrollbar : HScrollbar
	{
		height = 0;
	};
};

class RscFrame
{
	type = 0;
	idc = -1;
	style = 64;
	shadow = 2;
	colorBackground[] = {0,0,0,0};
	colorText[] = {1,1,1,1};
	font = "PuristaMedium";
	sizeEx = 0.02;
	text = "";
};

class rscText
{
	access = 0;
	type = 0;
	idc = -1;
	colorBackground[] = {0,0,0,0};
	colorText[] = {1,1,1,1};
	text = "";
	fixedWidth = 0;
	x = 0;
	y = 0;
	h = 0.037;
	w = 0.3;
	style = 0;
	shadow = 1;
	colorShadow[] = {0,0,0,0.5};
	font = "PuristaMedium";
	SizeEx = "(((((safezoneW / safezoneH) min 1.2) / 1.2) / 25) * 1)";
	linespacing = 1;
	tooltipColorText[] = {1,1,1,1};
	tooltipColorBox[] = {1,1,1,1};
	tooltipColorShade[] = {0,0,0,0.65};
};

