{
	{
		private _tempTarget = createSimpleObject ["Land_HelipadEmpty_F", getPosASL _x];
		[_tempTarget, nil, true] spawn BIS_fnc_moduleLightning;
		sleep (1 + random 4);
	} forEach (allPlayers call BIS_fnc_arrayShuffle);
};</sqf>

